import React, { useState } from 'react'
import { useNavigate } from 'react-router-dom'
import { Box, Typography, Paper } from '@mui/material'

import QuestionButton from './QuestionButton'
import questions from '../questions/questions'


const Question = () => {
    const [currentQuestion, setCurrentQuestion] = useState(questions[0])
    const currentAnswers = currentQuestion.answers

    const handleAnswerClick = (nextQuestion, calculatedCar) => {
        if (nextQuestion) {
            return setCurrentQuestion(questions[nextQuestion])
        }
        return navigate('/quiz-complete', {state: {calculatedCar: calculatedCar}})
    }        

    return (

// Questions
        <Box>
            <Box mb={5}>
                <Paper>
                    <Typography variant="h4">{currentQuestion.questionText}</Typography>
                </Paper>
            </Box>


{/* Answers */}
            <Box 
                width='100%'
                display='inline-flex'
            >                
                {currentAnswers?.map((test, index) => (
                    <QuestionButton 
                        text={currentAnswers[index].response}
                        calculatedCar={currentAnswers[index].calculatedCar}                                
                        onClick={() => handleAnswerClick(currentAnswers[index]?.nextQuestion, currentAnswers[index]?.calculatedCar)}
                    />
                ))}
            </Box>
        </Box>
    )
}

export default Question
