import React from 'react'
import { Link, useNavigate } from 'react-router-dom'
import { Box, Button, Typography } from '@mui/material'

function ComponentB() {
    const location = useLocation();   
    return location.state.calculatedCar
}


const QuestionButton = ({text, onClick, calculatedCar}) => {
    const regularButton = 
        (
            <Box 
                width='100%'
            >
                <Button 
                    variant="contained"
                >
                    <Typography variant="h6">
                        {text}
                    </Typography>
                </Button>
            </Box>
        )

    if (calculatedCar) { // if this question is the last one, turn the button into a link that sends 
        return (
            <Link 
                to='/quiz-complete'
                state={calculatedCar}
            >
                {regularButton}
            </Link>
        )
    }
    return regularButton
           
}

export default QuestionButton
