import React from 'react';
import { Box, Typography, Paper } from '@mui/material';
import Grid from '@mui/material/Grid2';

import QuestionButton from './QuestionButton';
import questions from '../questions/questions'

var currentQuestionIndex = 0

const changeQuestion = ({nextQuestionValue}) => {
    let currentQuestion = questions[nextQuestionValue]
}

const Question = () => {
    return (
        <Box>
            <Box>
                <Paper>
                    <Typography variant="h4">{questions[currentQuestionIndex].questionText}</Typography>
                </Paper>
            </Box>



            <Box>
                <Grid container spacing={2}>                    
                    <Grid item xs={6}>
                        {questions[currentQuestionIndex].answers.map((answer, index) => (
                            <QuestionButton 
                                text={answer[index].response}
                                onClick={changeQuestion(answer.nextQuestion)}
                            />
                        ))}
                    </Grid>
                </Grid>
            </Box>
        </Box>
    )
}

export default Question
