import React, { useState } from 'react'
import { Box, Typography, Paper } from '@mui/material'
import Grid from '@mui/material/Grid2'

import QuestionButton from './QuestionButton'
import questions from '../questions/questions'





var currentQuestionIndex = 0
var currentQuestion = questions[currentQuestionIndex]

const changeQuestion = ({nextQuestionValue}) => {
    currentQuestion = questions[nextQuestionValue]
}

const Question = () => {
    const [currentQuestion, setCurrentQuestion] = useState(questions[0])



    return (

// Questions
        <Box>
            <Box>
                <Paper>
                    <Typography variant="h4">{questions[currentQuestion].questionText}</Typography>
                </Paper>
            </Box>


{/* Answers */}
            <Box>
                <Grid container spacing={2}>                    
                    <Grid item xs={6}>
                        {questions[currentQuestion].answers.map((answer, index) => (
                            <QuestionButton 
                                text={answer[index].response}
                                onClick={setCurrentQuestion(answer.nextQuestion)}
                            />
                        ))}
                    </Grid>
                </Grid>
            </Box>


        </Box>
    )
}

export default Question
