import React from 'react';
import { Box, Typography, Paper } from '@mui/material';
import Grid from '@mui/material/Grid2';

import QuestionButton from './QuestionButton';
import questions from '../questions/questions'

var currentQuestionIndex = 0
var currentQuestion = questions[currentQuestionIndex]
const changeQuestion = ({nextQuestionValue}) => {
    currentQuestion = questions[currentQuestionIndex]
}

const Question = ({questionText, answerText}) => {
    return (
        <Box>
            <Box>
                <Paper>
                    <Typography variant="h4">{questions[currentQuestionIndex].questionText}</Typography>
                </Paper>
            </Box>



            <Box>
                <Grid container spacing={2}>                    
                    <Grid item xs={6}>
                        <QuestionButton text={questions[currentQuestionIndex].ans} onClick={changeQuestion(currentQuestionIndex, )}/>
                    </Grid>
                </Grid>
            </Box>
        </Box>
    )
}

export default Question
