import React from 'react'
import { Link, useNavigate } from 'react-router-dom'
import { Box, Button, Typography } from '@mui/material'



const RegularButton = (text) => {
    return (
        <Box 
            width='100%'
        >
            <Button 
                variant="contained"
            >
                <Typography variant="h6">
                    {text}
                </Typography>
            </Button>
        </Box>
    )  
}
       

const  LastQuestionButton = ({calculatedCar, text}) => {
    const navigate = useNavigate()
    const toQuizComplete = calculatedCar => {
        navigate('/quiz-complete', {state: {calculatedCar: calculatedCar}})
    }
    return (
        <Box onClick={()=>{toQuizComplete()}}>
            <regularButton text={text}/>
        </Box>
    )
}


const QuestionButton = ({text, calculatedCar}) => {
    if (calculatedCar) { // if this question is the last one, turn the button into a link that sends 
        return (
            <LastQuestionButton 
                calculatedCar={calculatedCar}
                text={text}
            />
        )
    }
    return <RegularButton text={text}/>
           
}

export default QuestionButton
