import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import { Box, Typography, Paper } from '@mui/material'

import QuestionButton from './QuestionButton'
import questions from '../questions/questions'


const Question = () => {
    const [currentQuestion, setCurrentQuestion] = useState(questions[0])
    const currentAnswers = currentQuestion.answers

    const handleAnswerClick = (nextQuestion) => {
        if (nextQuestion === 100) {
            window.location.href = '/quiz-complete'
            return
        }
        setCurrentQuestion(questions[nextQuestion])
    }
        

    return (

// Questions
        <Box>
            <Box>
                <Paper>
                    <Typography variant="h4">{currentQuestion.questionText}</Typography>
                </Paper>
            </Box>


{/* Answers */}
            <Box 
                width={'100%'} 
                display='inline-flex'
            >
                
                        {currentAnswers?.map((test, index) => (
                            <QuestionButton 
                                text={currentAnswers[index].response}
                                onClick={() => handleAnswerClick(currentAnswers[index].nextQuestion)}                               
                            />
                        ))}
            </Box>


        </Box>
    )
}

export default Question
